class SelekuClass{constructor(a){null===a?(console.log("Target tidak ditemukan."),this.elemen="Tidak ada elemen!"):this.elemen=a}style(a,b){const c=this.elemen.style;c[a]=b}tambahElemen(a,b){const c=document.createElement(a),d=document.createTextNode(b);c.appendChild(d);const e=this.elemen.appendChild(c);return e}isi(a){return this.isi.innerHTML(a)}tambahClass(a){const b=this.elemen.classList.add(a);return b}toggleClass(a){const b=this.elemen.classList.toggle(a);return b}log(){console.log(this.elemen)}pas(a,b){return this.elemen.addEventListener(a,b)}}function $elek(a,b){if("number"==typeof b){const c=document.querySelectorAll(a)[b];if(c===void 0)console.log("SelekuJS : indeks yang anda pilih tidak ada");else return c}else if("string"==typeof b){if("semua"===b){const b=document.querySelectorAll(a);return b}console.log("SelekuJS : Parameter tidak benar")}else{const b=document.querySelector(a);return b}}function $ku(a){const b=document.querySelector(a);return new SelekuClass(b)}